mapper.update:
update mail_log
set
is_deleted =
#{isDeleted,jdbcType=CHAR},
gmt_modified
=
#{gmtModified,jdbcType=TIMESTAMP},
modifier =
#{modifier,jdbcType=VARCHAR}
where
send_type=0

	AND gmt_created >=#{gmtCreatedStart,jdbcType=TIMESTAMP}


	AND gmt_created <=#{gmtCreatedEnd,jdbcType=TIMESTAMP}


	AND batch_Id = #{batchId,jdbcType=BIGINT}


	AND template_no = #{templateNo,jdbcType=VARCHAR}

update mail_log
set
send_type = #{sendType,jdbcType=TINYINT},
gmt_modified =
#{gmtModified,jdbcType=TIMESTAMP},
modifier =
#{modifier,jdbcType=VARCHAR}
where
send_type=0 and is_deleted='N'

	AND gmt_created >=#{gmtCreatedStart,jdbcType=TIMESTAMP}


	AND gmt_created <=#{gmtCreatedEnd,jdbcType=TIMESTAMP}


	AND batch_Id = #{batchId,jdbcType=BIGINT}


	AND template_no = #{templateNo,jdbcType=VARCHAR}

mapper.select:
select
from mail_log
order by id desc limit #{startRow,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}
